import 'dart:convert';class MolecularAllergen {  int id;  String name;  int molecularFamilyId;  String color;  MolecularAllergen(this.id, this.name, this.molecularFamilyId, this.color);  MolecularAllergen molecularAllergenFromJson(String str) {    final jsonData = json.decode(str);    return MolecularAllergen.fromJson(jsonData);  }  factory MolecularAllergen.fromJson(Map<String, dynamic> json){    return new MolecularAllergen(json["id"],json["name"],json["molecular_family_id"],json["color"]);  }  Map<String, dynamic> toJson() {    return {      'id': id,      'name': name,      'molecular_family_id' : molecularFamilyId,      'color':color,    };  }  Map<String, dynamic> toJsonNoId() {    return {      'name': name,      'molecular_family_id' : molecularFamilyId,      'color':color,    };  }}